import tkinter
import random
import sys
import time

key=""
def key_down(e):
    global key
    key = e.keysym
def key_up(e):
    global key
    key=""

mx=1
my=0
ex=10
ey=3

def main_proc():
    global mx, my
    if key=="Up" and maze[my-1][mx] == 0:
        my = my-1
        print("P : Up")
    if key == "Down" and maze[my+1][mx] == 0:
        my=my+1
        print("P : Down")
    if key == "Left" and maze[my][mx-1] == 0:
        mx = mx-1
        print("P : Left")
    if key=="Right" and maze[my][mx+1] == 0:
        mx=mx+1
        print("P : Right")
    if mx==ex and my==ey:
        Game_Over()
    canvas.coords("P", mx*20+10, my*20+10)
    root.after(100,main_proc)

def enemy():
    global ex,ey
    direc = random.randint(1,4)
    if direc==1 and maze[ey-1][ex] == 0 or ey-1==my and ex==mx:
        ey = ey-1
        print("E : Up")
    if direc==2 and maze[ey+1][ex] == 0 or ey+1==my and ex==mx:
        ey = ey+1
        print("E : Down")
    if direc==3 and maze[ey][ex-1] == 0 or ex-1==mx and ey==my:
        ex = ex-1
        print("E : Left")
    if direc==4 and maze[ey][ex+1] == 0 or ex+1==mx and ey==my:
        ex = ex+1
        print("E : Right")
    canvas.coords("E", ex*20+10, ey*20+10)
    root.after(100,enemy)

def Game_Over():
    print("System : Game Over")
    time.sleep(1)
    sys.exit()

